mapper.select:
SELECT MEMBER_NUMBER
FROM TBL_MEMBER
WHERE MEMBER_ID = #{memberId} and MEMBER_PASSWORD = #{memberPassword}

SELECT MEMBER_ID
FROM TBL_MEMBER
WHERE MEMBER_NUMBER = #{memberNumber}

mapper.update:
UPDATE TBL_MEMBER 
SET MEMBER_PASSWORD = #{memberPassword}, MEMBER_NICKNAME = #{memberNickname}, 
	MEMBER_ADDRESS_NUMBER = #{memberAddressNumber}, 
	MEMBER_EMAIL = #{memberEmail}, MEMBER_PHONE_NUMBER = #{memberPhoneNumber}
WHERE MEMBER_NUMBER = #{memberNumber}
